          cnt = 0;

          for ( i=1,j=2; i<largo; i++,j++ )
          {
            if ( cnt == 3 )
            {
              dtexto = dtexto + '.';
              j++;
              dtexto = dtexto + invertido.charAt(i);
              cnt = 1;
            }
            else
            {
              dtexto = dtexto + invertido.charAt(i);
              cnt++;
            }
          }

          invertido = "";
